import { onMounted, onUnmounted, reactive, computed } from 'vue';

export default {
  setup() {
    const state = reactive({
      screenWidth: window.innerWidth,
      minWidth: 800,
    });

    const isWide = computed(() => {
      return state.screenWidth >= state.minWidth;
    });

    const handleResize = () => {
      state.screenWidth = window.innerWidth;
