Per creare l'effetto di espansione e riduzione della tua card, puoi utilizzare una combinazione di animazioni, gesture e modificatori in SwiftUI. Ecco un esempio di come farlo nel file `ContentView.swift`:

```swift
import SwiftUI

struct Card: Identifiable {
    let id = UUID()
    let title: String
    let image: String
    let description: String
}

struct ContentView: View {
    let cards = [
        Card(title: "Titolo 1", image: "Image1", description: "Descrizione 1..."),
        Card(title: "Titolo 2", image: "Image2", description: "Descrizione 2..."),
        // Aggiungi altre card qui...
    ]

    @State private var selectedCard: Card?

    var body: some View {
        ZStack {
            // Grid di cards
            LazyVGrid(columns: [GridItem(.adaptive(minimum: 150))]) {
                ForEach(cards) { card in
                    CardView(card: card)
                        .onTapGesture {
                            withAnimation {
                                selectedCard = card
                            }
                        }
                }
            }

            // Card espansa
            if let card = selectedCard {
                ExpandingCardView(card: card, onClose: {
                    withAnimation {
                        selectedCard = nil
                    }
                })
            }
        }
    }
}

struct CardView: View {
    let card: Card

    var body: some View {
        VStack {
            Image(card.image)
                .resizable()
                .aspectRatio(contentMode: .fit)
            Text(card.title)
                .font(.headline)
                .padding()
        }
        .background(Color.white)
        .cornerRadius(10)
        .shadow(radius: 10)
        .padding()
    }
}

struct ExpandingCardView: View {
    @GestureState private var dragState = DragState.inactive

    let card: Card
    let onClose: () -> Void

    var body: some View {
        VStack {
            GeometryReader { geometry in
                VStack {
                    Image(card.image)
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                        .frame(maxHeight: geometry.safeAreaInsets.top + geometry.size.width)
                        .edgesIgnoringSafeArea(.top)
                        .overlay(CloseButton(onTap: onClose), alignment: .topTrailing)

                    ScrollView {
                        VStack(alignment: .leading) {
                            Text(card.title)
                                .font(.largeTitle)
                                .padding()

                            Text(card.description)
                                .font(.body)
                                .padding()
                        }
                    }
                }
            }
        }
        .background(Color.white)
        .cornerRadius(dragState.isActive ? 10 : 0)
        .shadow(radius: 10)
        .animation(.interpolatingSpring(stiffness: 200.0, damping: 25.0, initialVelocity: 10.0))
        .offset(y: dragState.isActive ? max(dragState.translation.height, 0) : 0)
        .opacity(dragState.isActive ? 1 - (dragState.translation.height / 1000) : 1)
        .scaleEffect(dragState.isActive ? max(1 - (dragState.translation.height / 1000), 0.9) : 1)
        .gesture(
            DragGesture()
                .updating($dragState) { value, state, _ in
                    state = DragState.active(translation: value.translation)
                }
                .onEnded { value in
                    if value.translation.height > 300 {
                        onClose()
                    }
                }
        )
    }
}

struct CloseButton: View {
    let onTap: () -> Void

    var body: some View {
        Button(action: onTap) {
            Circle()
                .frame(width: 30, height: 30)
                .foregroundColor(Color.white)
                .overlay(Image(systemName: "xmark").foregroundColor(Color.black))
                .padding()
        }
    }
}

enum DragState {
    case inactive
    case active(translation: CGSize)

    var isActive: Bool {
        switch self {
        case .inactive:
            return false
        case .active:
            return true
        }
    }

    var translation: CGSize {
        switch self {
        case .inactive:
            return .zero
        case .active(let translation):
            return translation
        }
    }
}

struct ContentView_Previews: PreviewProvider {
    static var previews: some View {
        ContentView()
    }
}
