          <input
            type="checkbox"
            :value="option.value"
            v-model="selectedValues"
            @change="handleParentChange(option)"
          />
          {{ option.label }}
        </label>
        <div class="custom-dropdown-submenu">
          <template v-if="option.children">
            <custom-dropdown
              :options="option.children"
              :selected-values="selectedValues"
              @input="handleChildChange"
            ></custom-dropdown>
          </template>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { option } from "./option";
export default {
  name: "CustomDropdown",
  props: {
    options: {
      type: Array,
      default: () => option,
    },
    selectedValues: {
      type: Array,
      default: () => [],
    },
  },
  data() {
    return {
      visible: false,
    };
  },
  methods: {
    handleParentChange(option) {
      if (this.selectedValues.includes(option.value)) {
        this.selectAllDescendants(option);
      } else {
        this.deselectAllDescendants(option);
      }
      this.$emit("input", this.selectedValues);
    },
    handleChildChange() {
      this.$emit("input", this.selectedValues);
    },
    selectAllDescendants(option) {
      if (option.children) {
        option.children.forEach((child) => {
          if (!this.selectedValues.includes(child.value)) {
            this.selectedValues.push(child.value);
          }
          this.selectAllDescendants(child);
        });
      }
    },
    deselectAllDescendants(option) {
      if (option.children) {
        option.children.forEach((child) => {
          const index = this.selectedValues.indexOf(child.value);
          if (index !== -1) {
            this.selectedValues.splice(index, 1);
          }
          this.deselectAllDescendants(child);
        });
      }
    },
  },
};
</script>
