  label: string;
}

const options: IOption[] = [
  { value: "option1", label: "Option 1" },
  { value: "option2", label: "Option 2" },
  { value: "option3", label: "Option 3" }
];

const ParentComponent: React.FC = () => {
  const [selectedOptions, setSelectedOptions] = useState<string[]>([]);

  const handleMultiSelectChange = (selected: string[]) => {
    setSelectedOptions(selected);
  };

  return (
    <div>
      <h1>Please select multiple options:</h1>
      <MultiSelect options={options} onChange={handleMultiSelectChange} />
      <p>Selected options: {selectedOptions.join(", ")}</p>
    </div>
  );
};

export default ParentComponent;
```

In this example, the `ParentComponent` renders the `MultiSelect` component with an array of options and a callback function (`handleMultiSelectChange`) to handle changes to the selected options.

The `handleMultiSelectChange` function updates the `selectedOptions` state whenever the user selects or deselects an option.

Finally, the `ParentComponent` renders the list of selected options as a comma-separated string.

Overall, this should be a fully functional parent component that allows the user to select multiple options from a dropdown list and displays the selected options in the parent component.

