namespace mediakit {

#define ErrorL std::cerr
#define InfoL std::cerr
#define WarnL std::cerr

class RollPollingWorker {
public:
	RollPollingWorker() : state_(kIdle), source_ctx_(nullptr), filter_ctx_(nullptr),
		output_(), mutex_(), ofmt_ctx_(nullptr), ocodec_ctx_(nullptr), need_idr_(false), frame_cnt_(0){};
	~RollPollingWorker() {};

	enum State {
		kIdle = 0,
		kRunning,
		kStoped
	};

	std::string Prefix() const {
		return "[roll polling][" + output_ + "] ";
	}

	int Start(const std::string input, const std::string output);
	int Stop();
	int UpdateInput(const std::string input);
	void Runnable();
	bool IsRunning() const { return state_ == kRunning; }

