		AVFormatContext* fmt_ctx_;
		AVCodecContext* codec_ctx_;
		AVFrame* decoded_frame_;
		int vid_stream_idx_;

		SourceContext() : fmt_ctx_(nullptr), codec_ctx_(nullptr), decoded_frame_(nullptr), vid_stream_idx_(-1) {};
		~SourceContext() {};
	};

	struct FilterContext {
		AVFilterGraph* filter_graph_;
		AVFilterContext* buffersrc_ctx_;
		AVFilterContext* buffersink_ctx_;
		AVFrame* filter_frame_;

		FilterContext() : filter_graph_(nullptr), buffersrc_ctx_(

int RollPollingWorker::EncodeFrame(AVFrame* frame) {
