		return -1;

	AVPacket* enc_pkt = av_packet_alloc();
	int ret = 0;
	while (ret >= 0) {
		ret = avcodec_receive_packet(ocodec_ctx_, enc_pkt);
        if (ret < 0) {
            if (ret == AVERROR(EAGAIN) || ret == AVERROR_EOF)
                ret = 0;
            break;
        }

		enc_pkt->stream_index = 0;
		enc_pkt->pts = enc_pkt->dts = (frame_cnt_++) * 40;
		enc_pkt->duration = 0;
		enc_pkt->pos = -1;

		//std::cerr << "muxing frame: pts " << enc_pkt->pts << " duration " << enc_pkt->duration << "\n";
		ret = av_interleaved_write_frame(ofmt_ctx_, enc_pkt);
		av_packet_unref(enc_pkt);
		if (ret < 0) {
