			break;
		}
	}

	av_packet_free(&enc_pkt);
	return ret;
}


int RollPollingWorkerPool::StartWorker(const std::string input, const std::string output) {
	std::shared_ptr<RollPollingWorker> worker = std::make_shared<RollPollingWorker>();
	if (!worker->Start(input, output)) {
		return -1;
	}

	std::weak_ptr<RollPollingWorker> weak_worker = worker;
	auto task = [weak_worker](int64_t unused){
		auto worker = weak_worker.lock();
		if (worker)
			worker->Runnable();
	};

	std::lock_guard<std::mutex> lock(mutex_);
	worker_list_[output] = worker;
	return 0;
}

int RollPollingWorkerPool::StopWorker(const std::string url) {
	std::lock_guard<std::mutex> lock(mutex_);
	auto it = worker_list_.find(url);
	return it != worker_list_.end() ? it->second->Stop() : -1;
}

int RollPollingWorkerPool::PollingStart(const std::vector<std::string> inputsurl, const std::string outputurl) {	
	std::vector<std::string> inputs = inputsurl ;
	// for (int i = 1; i < (argc - 1); i++)
	// 	inputs.push_back(argv[i]);
	// std::string output = argv[argc - 1];
	std::string output = outputurl;

	mediakit::RollPollingWorker worker;
	if (worker.Start(inputs[0], output) < 0)
		return -1;


	auto f = [&](){
		worker.Runnable();
	};

	std::thread task(f);
	task.detach();

	size_t idx = 1;
	while (worker.IsRunning()) {
		std::this_thread::sleep_for(std::chrono::milliseconds(10000));
		if (idx >= 4)
			break;

		if (inputs.size() > 1) {
			std::string tmp = inputs[idx % inputs.size()];
			std::cerr << "change input file to [" << tmp << "\n";
			worker.UpdateInput(tmp);
			idx++;			
		}
	}

	worker.Stop();
	//task.join();
	return 0;
