```C#
using System;
using System.Collections.Generic;

class Account
{
    private int accountNumber;
    private int accountBalance;

    public int AccountNumber
    {
        get { return accountNumber; }
        set { accountNumber = value; }
    }

    public int AccountBalance
    {
        get { return accountBalance; }
        set { accountBalance = value; }
    }
}

class Program
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        List<Account> accounts = new List<Account>();

        for (int i = 0; i < n; i++)
        {
            Account account = new Account();
            account.AccountNumber = int.Parse(Console.ReadLine());
            account.AccountBalance = int.Parse(Console.ReadLine());
            accounts.Add(account);
        }

        int searchAccountNum = int.Parse(Console.ReadLine());
        bool accountExists = false;

        foreach (Account account in accounts)
        {
            if (account.AccountNumber == searchAccountNum)
            {
                accountExists = true;
                Console.WriteLine(account.AccountBalance);
                break;
            }
        }

        if (!accountExists)
        {
            Console.WriteLine("Account Number Does Not Exist");
        }
    }
}
