```python
menu = {}

def add_item(name, price, description):
    menu[name] = {"price": price, "description": description}

def display_menu():
    print("Menu:")
    for name, item in menu.items():
        print(f"{name}: ${item['price']} - {item['description']}")

def change_item(name, new_name=None, new_price=None, new_description=None):
    if name not in menu:
        print(f"Item '{name}' not found.")
        return

    if new_name is not None:
        menu[new_name] = menu.pop(name)
        name = new_name

    item = menu[name]
    if new_price is not None:
        item['price'] = new_price
