import javax.swing.*;
import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

public class ChickenOrderSystem {
    public static void main(String[] args) {
        JFrame frame = new JFrame("Chicken Ordering System");

        JPanel panel = new JPanel();
        panel.setLayout(new BoxLayout(panel, BoxLayout.Y_AXIS));
        frame.add(panel);

        // Type of chicken
        JPanel typePanel = new JPanel(new GridLayout(4, 1));
        ButtonGroup typeGroup = new ButtonGroup();

        JRadioButton grilled = new JRadioButton("Grilled");
        JRadioButton fried = new JRadioButton("Fried");
        JRadioButton barbeque = new JRadioButton("Barbeque");
        JRadioButton roasted = new JRadioButton("Roasted");

        typeGroup.add(grilled);
        typeGroup.add(fried);
        typeGroup.add(barbeque);
        typeGroup.add(roasted);

        typePanel.add(grilled);
        typePanel.add(fried);
        typePanel.add(barbeque);
        typePanel.add(roasted);

        panel.add(new JLabel("Type of chicken:"));
        panel.add(typePanel);

        // Other components
        // ...

        // Submit button
        JButton submit = new JButton("Submit Order");
        panel.add(submit);

        submit.addActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                String chickenType = "";

                if (grilled.isSelected()) {
                    chickenType = "Grilled";
                } else if (fried.isSelected()) {
                    chickenType = "Fried";
                } else if (barbeque.isSelected()) {
                    chickenType = "Barbeque";
                } else if (roasted.isSelected()) {
                    chickenType = "Roasted";
                }

                // Process the remaining inputs
                // ...

                // Message to show the summary of the order
                String orderSummary = "Order Summary\n";
                orderSummary += "Type of chicken: " + chickenType;
                // Add other details to the order summary
                // ...

                JOptionPane.showMessageDialog(frame, orderSummary);
            }
        });

        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.pack();
        frame.setVisible(true);
    }
}
```

