  .append("svg")
  .attr("width", w)
  .attr("height", h);

// Create a rect for each data element
svg.selectAll("rect")
  .data(dataset)
  .enter()
  .append("rect")
  .attr("x", function(d, i) {
      return i * (w / dataset.length);
  })
  .attr("y", function(d) {
      return h - yScale(d);
  })
  .attr("width", w / dataset.length - barPadding)
  .attr("height", function(d) {
      return yScale(d);
  })
  .attr("fill", function(d) {
      return "rgb(0, 0, " + (d * 10) + ")";
  });

// Display text on each bar
