  .data(dataset)
  .enter()
  .append("text")
  .text(function(d) {
      return d;
  })
  .attr("x", function(d, i) {
      return i * (w / dataset.length) + 5;
  })
  .attr("y", function(d) {
      return h - yScale(d) - 5;
  })
  .attr("font-family", "sans-serif")
  .attr("font-size", "11px")
  .attr("fill", "white");
