      if (shaderProgram.luzPos1Loc != -1) gl.uniform3fv(shaderProgram.luzPos1Loc, posicionLuz1);
      if (shaderProgram.luzPos2Loc != -1) gl.uniform3fv(shaderProgram.luzPos2Loc, posicionLuz2);
      if (shaderProgram.colorLuz1Loc != -1) gl.uniform3fv(shaderProgram.colorLuz1Loc, colorLuz1);
