```java
import java.util.*;
import java.awt.*;
import java.awt.event.*;
import javax.swing.*;
import java.util.Random;

public class Game2048 extends JFrame implements ActionListener {
    private JButton[][] gridButtons;
    private int[][] grid;
    private Random random;

    public Game2048() {
        super("2048 Game");

        gridButtons = new JButton[4][4];
        grid = new int[4][4];
        random = new Random();

        JPanel gamePanel = new JPanel();
        gamePanel.setLayout(new GridLayout(4, 4));
        add(gamePanel, BorderLayout.CENTER);

        for (int i = 0; i < 4; i++) {
            for (int j = 0; j < 4; j++) {
                gridButtons[i][j] = new JButton();
                gridButtons[i][j].setFont(new Font("Arial", Font.BOLD, 32));
                gamePanel.add(gridButtons[i][j]);
            }
        }

        JPanel buttonPanel = new JPanel();
        buttonPanel.setLayout(new GridLayout(1, 3));
        add(buttonPanel, BorderLayout.SOUTH);

        JButton newGameButton = new JButton("New Game");
        buttonPanel.add(newGameButton);
        newGameButton.addActionListener(this);

        JButton quitButton = new JButton("Quit");
        buttonPanel.add(quitButton);
        quitButton.addActionListener(this);

        JButton ruleButton = new JButton("Rule");
        buttonPanel.add(ruleButton);
        ruleButton.addActionListener(this);

        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        setSize(400, 400);
        setVisible(true);
        newGame();
    }

    public void newGame() {
        for (int i = 0; i < 4; i++) {
            for (int j = 0; j < 4; j++) {
                grid[i][j] = 0;
                gridButtons[i][j].setText("");
                gridButtons[i][j].setBackground(new Color(252, 252, 252));
            }
        }

        int i1 = random.nextInt(4);
        int j1 = random.nextInt(4);
        grid[i1][j1] = 2;
        gridButtons[i1][j1].setText("2");
        gridButtons[i1][j1].setBackground(new Color(238, 228, 218));

        int i2 = random.nextInt(4);
        int j2 = random.nextInt(4);
        while (grid[i2][j2] != 0) {
            i2 = random.nextInt(4);
            j2 = random.nextInt(4);
        }
        grid[i2][j2] = 2;
        gridButtons[i2][j2].setText("2");
        gridButtons[i2][j2].setBackground(new Color(238, 228, 218));
    }

    public void actionPerformed(ActionEvent e) {
        String buttonLabel = e.getActionCommand();
        if (buttonLabel.equals("New Game")) {
            newGame();
        } else if (buttonLabel.equals("Quit")) {
            System.exit(0);
        } else if (buttonLabel.equals("Rule")){
            JOptionPane.showMessageDialog(this, "Use arrow keys to move tiles. When two tiles with the same number touch, they merge into one!");
        }
    }

    public void shiftUp() {
        for (int j = 0; j < 4; j++) {
            for (int i = 0; i < 3; i++) {
                if (grid[i][j] != 0) {
                    for (int k = i + 1; k < 4; k++) {
                        if (grid[k][j] != 0) {
                            if (grid[i][j] == grid[k][j]) {
                                grid[i][j] *= 2;
                                grid[k][j] = 0;
                                gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                                gridButtons[k][j].setText("");
                                gridButtons[i][j].setBackground(getColor(grid[i][j]));
                                gridButtons[k][j].setBackground(new Color(252, 252, 252));
                            }
                            break;
                        }
                    }
                }
            }
            for (int i = 0; i < 3; i++) {
                if (grid[i][j] == 0) {
                    for (int k = i + 1; k < 4; k++) {
                        if (grid[k][j] != 0) {
                            grid[i][j] = grid[k][j];
                            grid[k][j] = 0;
                            gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                            gridButtons[k][j].setText("");
                            gridButtons[i][j].setBackground(getColor(grid[i][j]));
                            gridButtons[k][j].setBackground(new Color(252, 252, 252));
                            break;
                        }
                    }
                }
            }
        }
        addNumber();
    }

    public void shiftDown() {
        for (int j = 0; j < 4; j++) {
            for (int i = 3; i > 0; i--) {
                if (grid[i][j] != 0) {
                    for (int k = i - 1; k >= 0; k--) {
                        if (grid[k][j] != 0) {
                            if (grid[i][j] == grid[k][j]) {
                                grid[i][j] *= 2;
                                grid[k][j] = 0;
                                gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                                gridButtons[k][j].setText("");
                                gridButtons[i][j].setBackground(getColor(grid[i][j]));
                                gridButtons[k][j].setBackground(new Color(252, 252, 252));
                            }
                            break;
                        }
                    }
                }
            }
            for (int i = 3; i > 0; i--) {
                if (grid[i][j] == 0) {
                    for (int k = i - 1; k >= 0; k--) {
                        if (grid[k][j] != 0) {
                            grid[i][j] = grid[k][j];
                            grid[k][j] = 0;
                            gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                            gridButtons[k][j].setText("");
                            gridButtons[i][j].setBackground(getColor(grid[i][j]));
                            gridButtons[k][j].setBackground(new Color(252, 252, 252));
                            break;
                        }
                    }
                }
            }
        }
        addNumber();
    }

    public void shiftLeft() {
        for (int i = 0; i < 4; i++) {
            for (int j = 0; j < 3; j++) {
                if (grid[i][j] != 0) {
                    for (int k = j + 1; k < 4; k++) {
                        if (grid[i][k] != 0) {
                            if (grid[i][j] == grid[i][k]) {
                                grid[i][j] *= 2;
                                grid[i][k] = 0;
                                gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                                gridButtons[i][k].setText("");
                                gridButtons[i][j].setBackground(getColor(grid[i][j]));
                                gridButtons[i][k].setBackground(new Color(252, 252, 252));
                            }
                            break;
                        }
                    }
                }
            }
            for (int j = 0; j < 3; j++) {
                if (grid[i][j] == 0) {
                    for (int k = j + 1; k < 4; k++) {
                        if (grid[i][k] != 0) {
                            grid[i][j] = grid[i][k];
                            grid[i][k] = 0;
                            gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                            gridButtons[i][k].setText("");
                            gridButtons[i][j].setBackground(getColor(grid[i][j]));
                            gridButtons[i][k].setBackground(new Color(252, 252, 252));
                            break;
                        }
                    }
                }
            }
        }
        addNumber();
    }

    public void shiftRight() {
        for (int i = 0; i < 4; i++) {
            for (int j = 3; j > 0; j--) {
                if (grid[i][j] != 0) {
                    for (int k = j - 1; k >= 0; k--) {
                        if (grid[i][k] != 0) {
                            if (grid[i][j] == grid[i][k]) {
                                grid[i][j] *= 2;
                                grid[i][k] = 0;
                                gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                                gridButtons[i][k].setText("");
                                gridButtons[i][j].setBackground(getColor(grid[i][j]));
                                gridButtons[i][k].setBackground(new Color(252, 252, 252));
                            }
                            break;
                        }
                    }
                }
            }
            for (int j = 3; j > 0; j--) {
                if (grid[i][j] == 0) {
                    for (int k = j - 1; k >= 0; k--) {
                        if (grid[i][k] != 0) {
                            grid[i][j] = grid[i][k];
                            grid[i][k] = 0;
                            gridButtons[i][j].setText(String.valueOf(grid[i][j]));
                            gridButtons[i][k].setText("");
                            gridButtons[i][j].setBackground(getColor(grid[i][j]));
                            gridButtons[i][k].setBackground(new Color(252, 252, 252));
                            break;
                        }
                    }
                }
            }
        }
        addNumber();
    }

    public void addNumber() {
        boolean moved = false;
        do {
            int i = random.nextInt(4);
            int j = random.nextInt(4);
            if (grid[i][j] == 0) {
                grid[i][j] = 2;
                gridButtons[i][j].setText("2");
                gridButtons[i][j].setBackground(new Color(238, 228, 218));
                moved = true;
            }
        } while (!moved);
    }

    public Color getColor(int value) {
        switch (value) {
            case 2:
                return new Color(238, 228, 218);
            case 4:
                return new Color(237, 224, 200);
            case 8:
                return new Color(242, 177, 121);
            case 16:
                return new Color(245, 149, 99);
            case 32:
                return new Color(246, 124, 95);
            case 64:
                return new Color(246, 94, 59);
            case 128:
                return new Color(237, 207, 114);
            case 256:
                return new Color(237, 204, 97);
            case 512:
                return new Color(237, 200, 80);
            case 1024:
                return new Color(237, 197, 63);
            case 2048:
                return new Color(237, 194, 46);
            default:
                return new Color(238, 228, 218);
        }
    }

    public static void main(String[] args) {
        new Game2048();
    }
}
```

