function betting(player, availableWords, pot) {
    if (availableWords.includes("FOLD")) {
        if (Math.random() < 0.5) {
            player.chips = 0;
        }
    } else if (availableWords.includes("BET") || availableWords.includes("RAISE")) {
        let betAmount = Math.floor(Math.random() * (player.chips / 2));
        player.chips -= betAmount;
        pot.add(betAmount);
    }
}

function randomBetting(players, availableWords, pot) {
    players.forEach(player => {
