        collider.size = new Vector2(0.01f, 0.01f);

        // Generate the bottom layer
        for (int i = 0; i < tileCount; i++)
        {
            GameObject bottomTile = Instantiate(tilePrefab);
            bottomTile.transform.position = new Vector3(i, bottomLayerHeight, 0);
            bottomTile.GetComponent<SpriteRenderer>().color = bottomLayerColor;

            TerrainHealth health = bottomTile.AddComponent<TerrainHealth>();
            health.health = Random.Range(minHealth, maxHealth);

            terrainTiles.Add(bottomTile);
        }

        float prevYPos = bottomLayerHeight;

        // Generate the rest of the tiles
        for (int i = 1; i < tileCount - 1; i++)
        {
            GameObject tile = Instantiate(tilePrefab);
            float noiseValue = Mathf.PerlinNoise(i * noiseScale, 0);

            float yPos = prevYPos + Random.Range(-heightRandomness, heightRandomness);
            yPos = Mathf.Clamp(yPos, bottomLayerHeight + 1, maxHeight);

            tile.transform.position = new Vector3(i, yPos, 0);

            // Set the size of the box collider
            collider.size = new Vector2(0.01f, 0.01f);

            // Fill the tiles between the terrain tile and the bottom layer tile
            for (int j = Mathf.FloorToInt(bottomLayerHeight) + 1; j < yPos; j++)
            {
                GameObject subTile = Instantiate(tilePrefab);
                subTile.transform.position = new Vector3(i, j, 0);

                TerrainHealth subHealth = subTile.AddComponent<TerrainHealth>();
                subHealth.health = Random.Range(minHealth, maxHealth);

                terrainTiles.Add(subTile);
            }

            // Add the health component to the terrain tile
            TerrainHealth health = tile.AddComponent<TerrainHealth>();
            health.health = Random.Range(minHealth, maxHealth);

            terrainTiles.Add(tile);

            prevYPos = yPos;
        }

        // Destroy the tile prefab
        Destroy(tilePrefab);
    }
}

