    public GameObject barnPrefab;
    public Transform barnSpawnPoint;
    public GameObject rubble;

    public void PurchaseBarn()
    {
        // Check if the player has enough currency to buy the barn.
        if (EconomyManager.CanPurchase(barnPrefab))
        {
            // Purchase the barn and add it to the player's inventory.
            EconomyManager.Purchase(barnPrefab);

            // Instantiate and position the barn at the designated spawn point.
            GameObject instantiatedBarn = Instantiate(barnPrefab, barnSpawnPoint.position, barnSpawnPoint.rotation);

            // Clear the rubble blocking the barn.
            ClearRubble();
        }
    }

    void ClearRubble()
    {
        // Destroy rubble or disable it based on your game design.
        Destroy(rubble);
    }
}
