extends KinematicBody

export var speed = 8
export var jump_strength = 12
export var max_slope_angle = 30.0

onready var animation_player = $AnimationPlayer

var velocity = Vector3()

func _physics_process(delta):
    # Inputs
    var move_input = Vector2()
    if Input.is_action_pressed("move_right"):
        move_input.x += 1
    if Input.is_action_pressed("move_left"):
        move_input.x -= 1
    if Input.is_action_pressed("move_forward"):
        move_input.y -= 1
    if Input.is_action_pressed("move_backward"):
        move_input.y += 1
    move_input = move_input.normalized()

    # Gamepad input
    var gamepad_move_input = Vector2(Input.get_joy_axis(0, JOY_AXIS_0), Input.get_joy_axis(0, JOY_AXIS_1))
    move_input += gamepad_move_input

    # Rotation
    if move_input.length() > 0:
        var target_angle = atan2(move_input.y, move_input.x)
        var rotation_angle = atan2(-transform.basis.z.x, transform.basis.x.x)
        var angle_diff = short_angle_to(target_angle, rotation_angle)
        rotate_y(angle_diff * 5 * delta)

        # Play walk animation
        animation_player.play("walk")
    else:
        # Play idle animation
        animation_player.play("idle")

    # Movement
    var dir = -transform.basis.z * move_input.y + transform.basis.x * move_input.x
    velocity.x = dir.x * speed
    velocity.z = dir.z * speed

    # Gravity
    velocity.y += delta * -20

    # Jump
    if Input.is_action_just_pressed("jump") and is_on_floor():
        velocity.y = jump_strength

    # Collision
    velocity = move_and_slide_with_snap(velocity, Vector3.DOWN * 0.1, Vector3.UP, true, Mathf.deg2rad(max_slope_angle))

func short_angle_to(a, b):
    var delta = fmod(b - a, 2 * PI)
    if delta > PI:
        delta -= 2 * PI
    elif delta < -PI:
        delta += 2 * PI
    return delta
```

