#include "exchange.hpp"

void Exchange::MakeDeposit(const std::string &username, const std::string &asset,
                           int amount) {
  users_[username].AddToPortfolio(asset, amount);
  exchange_portfolio_[asset] += amount;
}

void Exchange::PrintUserPortfolios(std::ostream &os) const {
  os << "User Portfolios:" << std::endl;
  for (const auto &user : users_) {
    os << user.first << ":" << std::endl;
    os << "   ";
    user.second.PrintPortfolio(exchange_portfolio_, os);
    os << std::endl;
  }
}

bool Exchange::MakeWithdrawal(const std::string &username,
                              const std::string &asset, int amount) {
  if (users_[username].CheckPortfolio(asset, amount)) {
    users_[username].RemoveFromPortfolio(asset, amount);
    exchange_portfolio_[asset] -= amount;
    return true;
  }
  return false;
}

bool Exchange::AddOrder(const Order &order) {
  if (order.side == "Buy") {
    for (auto itr = orders_.begin(); itr != orders_.end(); ++itr) {
      if (itr->asset == order.asset && itr->side == "Sell" &&
          itr->price <= order.price) {
        int trade_amount = std::min(order.amount, itr->amount);
        int trade_cost = trade_amount * itr->price;
        int fee = trade_cost * transaction_fee_;
        users_[order.username].RemoveFromPortfolio("USD", trade_cost + fee);
        users_[itr->username].AddToPortfolio("USD", trade_cost - fee);
        users_[order.username].AddToPortfolio(order.asset, trade_amount);
        users_[itr->username].RemoveFromPortfolio(itr->asset, trade_amount);
        exchange_portfolio_["USD"] += fee;
        exchange_portfolio_[order.asset] -= trade_amount;
        exchange_portfolio_[itr->asset] += trade_amount;
        trades_.push_back(Trade{order.username, itr->username, order.asset,
                                trade_amount, itr->price});
        if (order.amount > itr->amount) {
          order.amount -= itr->amount;
          itr = orders_.erase(itr);
          --itr;
        } else if (order.amount < itr->amount) {
          itr->amount -= order.amount;
          break;
        } else {
          orders_.erase(itr);
          break;
        }
      }
    }
    if (order.amount > 0) {
      orders_.push_back(order);
      std::sort(orders_.begin(), orders_.end(),
                [](const Order &a, const Order &b) {
                  if (a.price != b.price) {
                    return a.price > b.price;
                  } else if (a.side != b.side) {
                    return a.side == "Buy";
                  } else {
                    return a.username < b.username;
                  }
                });
    }
  } else if (order.side == "Sell") {
    for (auto itr = orders_.begin(); itr != orders_.end(); ++itr) {
      if (itr->asset == order.asset && itr->side == "Buy" &&
          itr->price >= order.price) {
        int trade_amount = std::min(order.amount, itr->amount);
        int trade_cost = trade_amount * order.price;
        int fee = trade_cost * transaction_fee_;
        users_[order.username].RemoveFromPortfolio(order.asset, trade_amount);
        users_[itr->username].AddToPortfolio(order.asset, trade_amount);
        users_[order.username].AddToPortfolio("USD", trade_cost - fee);
        users_[itr->username].RemoveFromPortfolio("USD", trade_cost + fee);
        exchange_portfolio_["USD"] += fee;
        exchange_portfolio_[order.asset] -= trade_amount;
        exchange_portfolio_[itr->asset] += trade_amount;
        trades_.push_back(
            Trade{itr->username, order.username, order.asset, trade_amount,
                  order.price});
        if (order.amount > itr->amount) {
          order.amount -= itr->amount;
          itr = orders_.erase(itr);
          --itr;
        } else if (order.amount < itr->amount) {
          itr->amount -= order.amount;
          break;
        } else {
          orders_.erase(itr);
          break;
        }
      }
    }
    if (order.amount > 0) {
      orders_.push_back(order);
      std::sort(orders_.begin(), orders_.end(),
                [](const Order &a, const Order &b) {
                  if (a.price != b.price) {
                    return a.price > b.price;
                  } else if (a.side != b.side) {
                    return a.side == "Buy";
                  } else {
                    return a.username < b.username;
                  }
                });
    }
  } else {
    return false;
  }
  return true;
}

void Exchange::PrintUsersOrders(std::ostream &os) const {
  os << "Users' Orders:" << std::endl;
  for (const auto &user : users_) {
    std::string username = user.first;
    os << username << ":" << std::endl;
    auto order_itr =
        std::find_if(orders_.begin(), orders_.end(),
                     [&](const Order &o) { return o.username == username; });
    auto trade_itr =
        std::find_if(trades_.begin(), trades_.end(),
                     [&](const Trade &t) {
                       return t.buyer_username == username ||
                              t.seller_username == username;
                     });
    while (order_itr != orders_.end() || trade_itr != trades_.end()) {
      if (order_itr != orders_.end() &&
          (trade_itr == trades_.end() ||
           order_itr->price >= trade_itr->price)) {
        os << "   ";
        order_itr->Print(os);
        os << std::endl;
        ++order_itr;
      } else {
        os << "   ";
        trade_itr->Print(username, os);
        os << std::endl;
        ++trade_itr;
      }
    }
  }
}

void Exchange::PrintTradeHistory(std::ostream &os) const {
  os << "Trade History:" << std::endl;
  for (const auto &trade : trades_) {
    os << "   ";
    trade.Print("", os);
    os << std::endl;
  }
}

void Exchange::PrintBidAskSpread(std::ostream &os) const {
  os << "Bid Ask Spread:" << std::endl;
  std::map<std::string, int> best_buy_prices;
  std::map<std::string, int> best_sell_prices;
  for (const auto &order : orders_) {
    std::string asset = order.asset;
    if (order.side == "Buy" && (best_buy_prices.count(asset) == 0 ||
                                order.price > best_buy_prices[asset])) {
      best_buy_prices[asset] = order.price;
    } else if (order.side == "Sell" && (best_sell_prices.count(asset) == 0 ||
                                         order.price < best_sell_prices[asset])) {
      best_sell_prices[asset] = order.price;
    }
  }
  for (const auto &asset_pair: best_buy_prices) {
    std::string asset = asset_pair.first;
    if (best_sell_prices.count(asset) > 0) {
      os << "   " << asset_pair.first << ": " << asset_pair.second << " / " << best_sell_prices[asset] << std::endl;
    }
  }
}
