describe('test OutstandingTransactions', () => {
  beforeEach(() => {
    jest.spyOn(TotalsSelectors, 'getSubTotal').mockImplementation(() => '22.00');
    jest.spyOn(paymentTableSelectors, 'getCharges')
      .mockImplementation(() => [{ isLinkedPaymentAmount: true, linkedPaymentAmount: '10' }]);
  });

  test('Total paid amount should be updated when charges are linked', async () => {
    const variability = { environment: { txnType: TransactionTypes.PURCHASE_BILL } };
    const txpInstance = TransactionsCoreTestUtils.createMinimumTransactionTXPInstance(
      [outstandingTransactionsModule],
      variability
    );

    await runtimeTestUtils.renderTXPLayout(
      <TransactionHandler>
        <PaymentTable idsProps={{}} />
      </TransactionHandler>,
      txpInstance
    );

    const linkedPaymentAmountInput = screen.getByTestId('linked-payment-amount-0');
    fireEvent.change(linkedPaymentAmountInput, { target: { value: '10.00' } });
    fireEvent.blur(linkedPaymentAmountInput);

    await waitFor(() => {
      expect(screen.getByTestId('total-paid-amount').textContent).toEqual('10.00');
    });
  });

  test('Total paid amount should not be updated when charges are not linked', async () => {
    jest.spyOn(paymentTableSelectors, 'getCharges')
      .mockImplementation(() => [{ isLinkedPaymentAmount: false, linkedPaymentAmount: '10' }]);

