        value={taskTitle}
        onChange={handleTitleChange}
        className="w-2/3 px-3 py-2 border border-gray-400 rounded-md"
      />
      <div className="flex items-center space-x-4">
        <label>
          <input
            type="radio"
            name="task-type"
            value="front-end"
            checked={taskType === 'front-end'}
