                    $currentDate->addDay();
                    continue;
                }

                $startTime = $service->name === 'Men Haircut' && $currentDate->isSaturday() ? '10:00:00' : '08:00:00';
                $endTime = $service->name === 'Men Haircut' && $currentDate->isSaturday() ? '22:00:00' : '20:00:00';
                $duration = $service->duration;

                // Define slots and OffTimes for the day
                for ($time = strtotime($startTime); $time < strtotime($endTime); $time = strtotime("+$duration minutes", $time)) {
                    $slots->push([
                        'service_id' => $service->id,
                        'day' => $currentDate->englishDayOfWeek,
                        'start_time' => date('H:i:s', $time),
                        'end_time' => date('H:i:s', strtotime("+10 minutes", $time)),
                    ]);

                    // Add cleanup break
