weighted_avg_time_limited <- function(surv_probs, surv_times) {
  if (length(surv_probs) == 0 || length(surv_times) == 0 || sum(surv_probs) == 0) {
    return(0)
  }
  weighted_time <- sum(surv_probs * surv_times) / sum(surv_probs)
  return(weighted_time)
}
