```csharp
using Microsoft.Identity.Client;
using Microsoft.Graph;
using System;
using System.Windows.Forms;
using Outlook = Microsoft.Office.Interop.Outlook;

namespace [YOUR_NAMESPACE]
{
    public partial class OutageCalendarPane : UserControl
    {
        private const string ClientId = "[YOUR_APP_REGISTRATION_CLIENT_ID]";
        private const string TenantId = "[YOUR_TENANT_ID]";
        private const string UtilityAccountUsername = "outage-helper@ct4.com";
        private const string UtilityAccountPassword = "[PASSWORD]";
        private GraphServiceClient _graphClient;
        private Outlook.MailItem _selectedMailItem;

        public OutageCalendarPane(Outlook.MailItem selectedMailItem)
        {
            InitializeComponent();
            _selectedMailItem = selectedMailItem;
            emailContentTextBox.Text = selectedMailItem.Body;
            AuthenticateAndInitializeGraphClient().ConfigureAwait(false);
        }

        private async System.Threading.Tasks.Task AuthenticateAndInitializeGraphClient()
        {
            var app = PublicClientApplicationBuilder
                .Create(ClientId)
                .WithAuthority(AzureCloudInstance.AzurePublic, TenantId)
                .Build();

            var securePassword = new System.Security.SecureString();
            foreach (char c in UtilityAccountPassword) securePassword.AppendChar(c);

            var authResult = await app.AcquireTokenByUsernamePassword(new[] { "https://graph.microsoft.com/.default" }, UtilityAccountUsername, securePassword).ExecuteAsync();

            _graphClient = new GraphServiceClient(new DelegateAuthenticationProvider(requestMessage => {
                requestMessage.Headers.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", authResult.AccessToken);
                return System.Threading.Tasks.Task.CompletedTask;
            }));
        }

        private async void submitBtn_Click(object sender, EventArgs e)
        {
            if (_graphClient != null)
            {
                string calendarId = "[OUTAGE-CALENDAR-ID]";
                var newEvent = new Event
                {
                    Subject = eventNameTextBox.Text,
                    Start = new DateTimeTimeZone { DateTime = startDateTimePicker.Value.ToString("o"), TimeZone = "UTC" },
                    End = new DateTimeTimeZone { DateTime = endDateTimePicker.Value.ToString("o"), TimeZone = "UTC" },
                    Body = new ItemBody
                    {
                        ContentType = BodyType.Text,
                        Content = $"Username: {_selectedMailItem.Sender.Name}\nNote: {eventNoteTextBox.Text}\n\nEmail Text: {emailContentTextBox.Text}"
                    }
                };

                await _graphClient.Users[UtilityAccountUsername].Calendars[calendarId].Events.Request().AddAsync(newEvent).ConfigureAwait(false);
                MessageBox.Show("Outage event has been added to the shared calendar.");
                ((Form)this.TopLevelControl).Close();
            }
            else
            {
                MessageBox.Show("Error: Unable to add the outage event. Please try again.");
            }
        }

        private void cancelBtn_Click(object sender, EventArgs e)
        {
            ((Form)this.TopLevelControl).Close();
        }
    }
}
```

