namespace Outage_Helper
{
    public partial class OutageCalendarPane : UserControl
    {
        private const string ClientId = “XXXX”;
        private const string TenantId = “XXXX”;
        private const string UtilityAccountUsername = “outage-helper@ct4.com”;
        private const string UtilityAccountPassword = “XXXX”;
        private GraphServiceClient _graphClient;
        private Outlook.MailItem _selectedMailItem;

        public OutageCalendarPane(Outlook.MailItem selectedMailItem)
        {
            InitializeComponent();
            _selectedMailItem = selectedMailItem;
            emailContentTextBox.Text = selectedMailItem.Body;
            AuthenticateAndInitializeGraphClient().ConfigureAwait(false);

            submitBtn.Click += submitBtn_Click;
