      <div className="flex justify-between">
        <div className="w-1/2 mr-4">
          <h2 className="mb-2 font-bold text-lg">Front-End Tasks</h2>
          {frontEndTasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              onTaskDelete={deleteTask}
              onShowSubtaskModal={handleShowSubtaskModal}
            />
          ))}
        </div>
        <div className="w-1/2">
          <h2 className="mb-2 font-bold text-lg">Back-End Tasks</h2>
          {backEndTasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              onTaskDelete={deleteTask}
              onShowSubtaskModal={handleShowSubtaskModal}
            />
          ))}
        </div>
      </div>
      {showSubtaskModal && (
        <SubtaskModal
          task={selectedTask}
          onCloseModal={handleHideSubtaskModal}
          onAddSubtask={addSubtask}
        />
      )}
    </div>
  );
}

export default TaskContainer;

