using System;
using System.Collections.Generic;

namespace DirectedEdgeClassification
{
    class Graph
    {
        Dictionary<string, List<string>> AdjList;
        Dictionary<string, Color> colors;
        Dictionary<string, int> discoveryTime;
        Dictionary<string, int> finishingTime;
        int time;

        enum EdgeType
        {
            Tree,
            Forward,
            Back,
            Cross
        }

        enum Color
        {
            White,
            Gray,
            Black
        }

        public Graph()
        {
            AdjList = new Dictionary<string, List<string>>();
            colors = new Dictionary<string, Color>();
            discoveryTime = new Dictionary<string, int>();
            finishingTime = new Dictionary<string, int>();
            time = 0;
        }

        public void AddEdge(string u, string v)
        {
            if (!AdjList.ContainsKey(u))
            {
                AdjList[u] = new List<string>();
                colors[u] = Color.White;
                discoveryTime[u] = 0;
                finishingTime[u] = 0;
            }
            AdjList[u].Add(v);
        }

        public void DFS()
        {
            foreach (var node in AdjList.Keys)
            {
                if (colors[node] == Color.White)
                {
                    DFSVisit(node);
                }
            }
        }

        private void DFSVisit(string u)
        {
            colors[u] = Color.Gray;
            time++;
            discoveryTime[u] = time;

            foreach (var v in AdjList[u])
            {
                if (!AdjList.ContainsKey(v))
                {
                    AdjList[v] = new List<string>();
                    colors[v] = Color.White;
                    discoveryTime[v] = 0;
                    finishingTime[v] = 0;
                }

                EdgeType edgeType = ClassifyEdge(u, v);
                Console.WriteLine("Edge ({0}, {1}): {2}", u, v, edgeType);

                if (colors[v] == Color.White)
                {
                    DFSVisit(v);
                }
            }

            colors[u] = Color.Black;
            time++;
            finishingTime[u] = time;
        }

        private EdgeType ClassifyEdge(string u, string v)
        {
            if (colors[v] == Color.White)
            {
                return EdgeType.Tree;
            }
            if (colors[v] == Color.Gray)
            {
                return EdgeType.Back;
            }
            if (colors[v] == Color.Black)
            {
                if (discoveryTime[u] < discoveryTime[v])
                {
                    return EdgeType.Forward;
                }
                if (discoveryTime[u] > discoveryTime[v])
                {
                    return EdgeType.Cross;
                }
            }

            throw new InvalidOperationException("Unclassified edge.");
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            Graph graph = new Graph();
            graph.AddEdge("A", "B");
            graph.AddEdge("A", "C");
            graph.AddEdge("B", "D");
            graph.AddEdge("D", "A");
            graph.AddEdge("E", "D");

            graph.DFS();
        }
    }
}
```

