```rust
extern crate embedded_hal;
extern crate esp32_hal;

use core::convert::Infallible;
use embedded_hal::prelude::*;
use embedded_hal::durations::U32Ext;
use esp32_hal::{
    clock_control::{self, ClockControl, CPUSource},
    gpio::{self, Gpio18, Input, Output, SoftwareControlled, PullUp},
    pwm::*,
    timer::*,
};
use panic_halt as _;

const SERVO_MIN_PULSE_WIDTH_US: u32 = 500;
const SERVO_MAX_PULSE_WIDTH_US: u32 = 2400;

type ServoSignal = Pwm2<pwm::Timer2<pwm::PrescaleAndOverflowCalculator16Bits>>;
type ServoGpio = Gpio18<SoftwareControlled<Output>>;

fn main() {
    let mut peripherals = clock_control::Peripherals::take().unwrap();
    let (clock_control, mut watchdog) = ClockControl::new(peripherals.clock_control, peripherals.rtc_control, &mut peripherals.apb_control, CPUSource::PLL, clock_control::XTAL_FREQUENCY_AUTO).unwrap();

    let (timer2, channel2) = peripherals.pwm2.split();
    let timer2_config = Timer::configure(timer2, Timer2Params::default());

    let pads = gpio::Pads::default();
    let servo_gpio = pads.get::<Gpio18>().unwrap().into_output();
    let servo_pwm = timer2_config.channel2(servo_gpio, channel2);

    let mut pwm_servo = Servo::new(servo_pwm);

    pwm_servo.set_angle(90).unwrap();

    loop {
        watchdog.feed();
    }
}

struct Servo {
    pwm: ServoSignal,
    gpio: ServoGpio,
}

impl Servo {
    fn new(pwm: ServoSignal, gpio: ServoGpio) -> Self {
        Servo { pwm, gpio }
    }

    fn set_angle(&mut self, angle: u8) -> Result<(), Infallible> {
        let pulse_width_us = map_range(angle, 0, 180, SERVO_MIN_PULSE_WIDTH_US, SERVO_MAX_PULSE_WIDTH_US);
        self.pwm.set_pulse_width(1_000_000u32, pulse_width_us)
    }
}

fn map_range(value: u8, in_min: u8, in_max: u8, out_min: u32, out_max: u32) -> u32 {
    let value = value as u32;
    let in_min = in_min as u32;
    let in_max = in_max as u32;
    (value - in_min) * (out_max - out_min) / (in_max - in_min) + out_min
}

```

