public abstract class BaseJobHandlerService {

    private final Logger logger = LoggerFactory.getLogger(BaseJobHandlerService.class);

    @Value("${cron.job.orgId}")
    private String orgId;

    @Value("${spring.token.appKey}")
    private String appKey;

    @Value("${spring.token.appSecret}")
    private String appSecret;

    @Value("${cron.job.userId}")
    private String userId;

    @Value("${cron.job.username}")
    private String username;

    @Autowired
    private V3OpenClient v3OpenClient;

    public void handle(String type, boolean isFailed, String paramStr) {
        // 一 获取header
        HeaderDTO header = this.getHeader(type);
        // 三 getAccessToken
        String token = getAccessToken(header);
        header.setToken(token);

        // 二 获取最新同步时间
        logger.info("type: {}, param: {}", type, paramStr);
        String fromAt;
        String fromTo;
        if (StringUtils.isBlank(paramStr)) {
            fromAt = this.getSyncTime(type, header);
            fromTo = TimeUtils.longToDatetime(System.currentTimeMillis() - 60000).orElseThrow(() -> new RuntimeException("获取当前时间失败"));
        } else {
            String[] params = paramStr.split("\\|");
            fromAt = params[0];
            fromTo = params[1];
        }

        // 四 获取需要发送的数据
        List<?> dataList = this.getData(type, fromAt, fromTo, header);
        List<ERPSyncResultRequestDTO> result = Collections.emptyList();
        if (!dataList.isEmpty()) {
            this.sendData(type, dataList, header);
        }
        if (StringUtils.isBlank(paramStr)) {
            v3OpenClient.updateSyncTime(type, fromTo, header);
        }
    }

    public abstract List<?> getData(String type, String fromAt, String fromTo, HeaderDTO header);

    public abstract void sendData(String type, List<?> dataList, HeaderDTO header);

