```python
import os
import hashlib

# 递归遍历目录，获取文件的元数据信息
def get_file_metadata(path):
    metadata = {}
    try:
        if os.path.isfile(path):
            metadata['path'] = path
            metadata['size'] = os.path.getsize(path)
            metadata['modified_time'] = os.path.getmtime(path)
            with open(path, 'rb') as f:
                metadata['hash'] = hashlib.md5(f.read()).hexdigest() # 获取文件内容的md5值
        elif os.path.isdir(path):
            metadata['type'] = 'dir'
            metadata['path'] = path
            metadata['contents'] = []
            for filename in os.listdir(path):
                sub_path = os.path.join(path, filename)
                metadata['contents'].append(get_file_metadata(sub_path))
    except Exception as e:
        print('error:', e)
    return metadata

# 获取同步前后的目录结构并比对
def compare_dir_structure(before_dir, after_dir):
    before_metadata = get_file_metadata(before_dir)
    after_metadata = get_file_metadata(after_dir)

    def compare_files(before_file, after_file):
        if before_file['hash'] != after_file['hash']:
            print('file mismatch:', before_file['path'], after_file['path'])
        elif before_file['modified_time'] != after_file['modified_time']:
            print('file timestamp mismatch:', before_file['path'], after_file['path'])

    def compare_dirs(before_dir, after_dir):
        before_path = before_dir['path'].replace(before_dir, after_dir, 1)
        after_path = after_dir['path'].replace(after_dir, before_dir, 1)

        for before_file in before_dir['contents']:
            for after_file in after_dir['contents']:
                if before_file['path'] == after_file['path']:
                    if before_file['type'] == 'file' and after_file['type'] == 'file':
                        compare_files(before_file, after_file)
                    elif before_file['type'] == 'dir' and after_file['type'] == 'dir':
                        compare_dirs(before_file, after_file)

        for before_file in before_dir['contents']:
            if before_file['path'].startswith(before_path):
                print('file added:', before_file['path'])
        for before_file in before_dir['contents']:
            if not any(after_file['path'] == before_file['path'] for after_file in after_dir['contents']):
                print('file deleted:', before_file['path'])

    compare_dirs(before_metadata, after_metadata)
