disp(x);

% Define the function for the Jacobi iteration
function x = jacobi_iteration(A, B, tolerance, max_iterations)
    n = length(B);
    x = zeros(n, 1);
    x_prev = x;

    for k = 1:max_iterations
        for i = 1:n
            x(i) = (B(i) - A(i, [1:i-1, i+1:n]) * x_prev([1:i-1, i+1:n])) / A(i, i);
        end

        % Check the convergence
        if max(abs(x - x_prev)) < tolerance
            break;
        end

        x_prev = x;
    end
end

% Plot the obtained solution
bar(x);
title('Solution of the System of Linear Equations using Jacobi Method');
xlabel('Variables (x1, x2, x3)');
ylabel('Values');
xticklabels({'x1', 'x2', 'x3'});
```

