void Manager::init_parameters()
{
  // Declare and get parameters
  this->declare_parameter("lidar.scan_topic", "/lidar/scan");
  this->declare_parameter("filtered.scan_topic", "/lidar/filtered_scan");
  this->declare_parameter("lidar_frame_id", "lidar_link");

  this->get_parameter("lidar.scan_topic", lidar_scan_topic_);
  this->get_parameter("filtered.scan_topic", filtered_scan_topic_);
  this->get_parameter("lidar_frame_id", lidar_frame_id_);
}

void Manager::init_publishers()
{
  // Create filtered scan publisher
  filtered_scan_pub_ = this->create_publisher<sensor_msgs::msg::LaserScan>(filtered_scan_topic_, 10);
}

void Manager::init_subscribers()
{
  // Create raw lidar scan subscriber
  lidar_scan_sub_ = this->create_subscription<sensor_msgs::msg::LaserScan>(lidar_scan_topic_, 10,
    std::bind(&Manager::lidar_scan_callback, this, std::placeholders::_1));
}

void Manager::lidar_scan_callback(const sensor_msgs::msg::LaserScan::SharedPtr msg)
{
  // Process incoming lidar scan data
  sensor_msgs::msg::LaserScan::SharedPtr filtered_scan = this->process_lidar_scan(msg);

  // Publish filtered lidar scan data
  filtered_scan_pub_->publish(*filtered_scan);
}

sensor_msgs::msg::LaserScan::SharedPtr Manager::process_lidar_scan(sensor_msgs::msg::LaserScan::SharedPtr msg)
{
  // Create a new message for filtered scan
  auto filtered_scan = std::make_shared<sensor_msgs::msg::LaserScan>(*msg);

  // Process lidar scan data according to your needs
  // Here, simply consider the range_max as 10 (for example)
  for (auto& range : filtered_scan->ranges)
  {
    if (range > 10.0)
    {
      range = 10.0;
    }
  }

  // Set the header frame_id
  filtered_scan->header.frame_id = lidar_frame_id_;

  return filtered_scan;
}

