Here's a simple example of MATLAB code that uses the Intelligent Driver Model (IDM) to simulate traffic flow on a single road lane with cars of different lengths and animates the results:

```matlab
% Parameters

num_cars = 10;
road_length = 1000; % meters
simulation_time = 20; % seconds
time_step = 0.1;
num_time_steps = simulation_time / time_step;

% IDM parameters

a = 1.4; % maximum acceleration
b = 2; % comfortable braking deceleration
delta = 4; % acceleration exponent
s0 = 2; % minimum desired following distance
T = 1.5; % safe time headway
global v0; % desired speed

% Car parameters

car_lengths = randi([4, 15], 1, num_cars); % lengths in (4,15) meters
car_positions = linspace(0, road_length, num_cars+1) - cumsum(car_lengths) - (s0+T*v0);
car_velocities = ones(1, num_cars).*v0;

% IDM implementation

% Calculate acceleration given vehicle distance, speed, and leader speed
function acc = idm_distance(x, v, vl)
    global a b delta s0 T v0;
    s_star = s0 + v*T + v*(v-vl)/(2*sqrt(a*b));
    acc = a*(1 - (v/v0)^delta - (s_star./x).^2);
end

% Set up the display
figure;
h = animatedline;
axis([0, road_length, -5, 5])

% Run simulation
for t = 1:num_time_steps
    for i = 1:num_cars
        if i == 1
            inter_vehicle_distance = car_positions(end) + road_length - car_positions(1) - car_lengths(1);
            car_accel = idm_distance(inter_vehicle_distance, car_velocities(i), car_velocities(end));
        else
            inter_vehicle_distance = car_positions(i) - car_positions(i-1) - car_lengths(i);
            car_accel = idm_distance(inter_vehicle_distance,car_velocities(i), car_velocities(i-1));
        end
        car_velocities(i) = car_velocities(i) + time_step*car_accel;
        car_positions(i) = car_positions(i) + time_step*car_velocities(i);
    end

    % Wrap positions around road
    car_positions = mod(car_positions, road_length);

    % Sort positions and corresponding lengths and velocities
    [car_positions, sort_idxs] = sort(car_positions);
    car_lengths = car_lengths(sort_idxs);
    car_velocities = car_velocities(sort_idxs);

    % Update display
    clearpoints(h)
    for i = 1:num_cars
        addpoints(h, car_positions(i), 0)
        % Plot the rectangle representing the car
        rectangle('Position', [car_positions(i), -car_lengths(i)/2, car_lengths(i), car_lengths(i)], 'FaceColor', 'b', 'EdgeColor', 'k')
    end

    drawnow;
    pause(time_step);
end
