car_lengths = randi([3, 7], [1, num_cars]); % lengths of each car (in meters)
driver_react_time = 1.5; % driver reaction time (in seconds)
desired_speed = 30; % desired speed (in m/s)
safe_distance = 5; % minimum safe distance between cars (in meters)
max_acceleration = 3; % maximum acceleration (in m/s^2)

% initialize car positions, velocities, and accelerations
positions = linspace(0, road_length, num_cars+2); % add extra space for boundary cars
velocities = desired_speed * ones(1, num_cars);
accelerations = zeros(1, num_cars);

% set up animation
fig = figure();
xlim([0, road_length]);
ylim([-2, max(car_lengths)]);
framesPerSecond = 30;
dt = 1/framesPerSecond;

% simulate traffic flow
for t = 0:dt:10 % simulate for 10 seconds
    % calculate accelerations of each car
    for i = 1:num_cars
        pos_diff = positions(i) - positions(i+1);
        vel_diff = velocities(i) - velocities(i+1);
        spacing = pos_diff - car_lengths(i)/2 - car_lengths(i+1)/2;

        % IDM acceleration function
        s_star = safe_distance + velocities(i) * driver_react_time;
        accel_term1 = max_acceleration * (1 - (velocities(i)/desired_speed)^4);
        accel_term2 = max_acceleration * ((s_star/spacing)^2);
        accelerations(i) = accel_term1 - accel_term2;

        % bound acceleration to limit
        accelerations(i) = max(-max_acceleration, min(max_acceleration, accelerations(i)));
    end

    % update car velocities and positions
    velocities = velocities + accelerations * dt;
    positions = positions + velocities * dt;

    % draw cars at new positions
    clf; % clear figure
    for i = 1:num_cars
        rectangle('Position', [positions(i)-(car_lengths(i)/2), -(car_lengths(i)/2), car_lengths(i), car_lengths(i)], 'FaceColor', 'b');
    end

    drawnow; % display changes in figure
    pause(dt); % delay to match desired frame rate
end
