namespace DeepEnergyHyperelasticity
{
    class Program
    {
        static void Main(string[] args)
        {
            // Define the hyperparameters
            int epochs = 100;
            double learningRate = 0.01;
            int inputSize = 3;
            int hiddenSize = 10;
            int outputSize = 1;

            using (var d = new TorchSharp.TorchDevice(TorchSharp.TorchDeviceType.CPU))
            {
                var x = LoadDeformationData(); // Load your input data (deformation)
                var y = LoadEnergyData(); // Load your output data (energy)

                // Normalize the data if needed

                // Define the network architecture
                var model = new Sequential(
                        new Linear(inputSize, hiddenSize),
                        new ReLU(),
                        new Linear(hiddenSize, hiddenSize),
                        new ReLU(),
                        new Linear(hiddenSize, outputSize)
                    ).To(d);

                // Create a loss function (Mean Squared Error)
                var lossFunction = nn.MSELoss().To(d);

                // Create an optimizer (Stochastic Gradient Descent)
