# Clear any previous file
clear;

# Constants
lambda0 = 1550e-9;                  # Central wavelength
radius = 10e-6;                     # Ring resonator radius
gap = 200e-9;                       # Gap between waveguide and ring resonator
waveguide_width = 500e-9;           # Waveguide width
n_ring = 2.2;                       # Refractive index of the ring resonator
n_clad = 1.44;                      # Refractive index of the cladding
neff_guess = 1.9;                   # Effective index guess for mode source
grating_period = 630e-9;            # Grating period
grating_ff = 0.5;                   # Grating fill factor
num_gratings = 5;                   # Number of grating elements
grating_width = num_gratings * grating_period;

# Set up simulation region
set('dimension', 3);
set('x', 0); 
set('y', 0);
set('x span', 30e-6);
set('y span', 30e-6);
set('z span', 2e-6);
set('simulation time',2000e-15);

# Add a waveguide 
addrect;
set('name', 'Waveguide');
set('x span', 10e-6);
set('y span', waveguide_width);
set('z', 0);
set('z span', 200e-9);
set('material','Si (Silicon)');

# Add the ring resonator with grating
adddisc;
set('name', 'RingResonator');
set('material','Si (Silicon)');
set('radius', radius);
set('z', 0);
set('z span', 200e-9);

# Add the grating elements
for (idx = 0; idx < num_gratings; idx++) {
    addrect;
    set('name', 'Grating_' + num2str(idx));
    set('x', lambda0 * idx);
    set('x span', grating_width / num_gratings);
    set('y', radius - gap - waveguide_width/2);
    set('y span', waveguide_width * grating_ff);
    set('z', 0);
    set('z span', 200e-9);
    set('material', grating_material);
    set('index', grating_index);
}

# Set up the mode source
addmodesource;
set('name', 'Modesource');
set('x', -5e-6);
set('y', 0);
set('y span',waveguide_width);
set('wavelength span',100e-9);
set('center wavelength',lambda0);
set('direction', 1);
set('total field', 1);
set('number of trial modes', 1);
setmode(1, 'TE');
set('I add loss', 0);
set('inject');
set('index', 1.44);

# Set up the transmission monitor
addpower;
set('name', 'Transmission_Monitor');
set('x', 5e-6);
set('y', 0);
set('y span', waveguide_width);
set('sample spacing', 1);
set('wavelength stop', 1600e-9);
set('wavelength start', 1500e-9);
set('frequency points', 50);
set('monitor type', '2D Z only');
set('number of field profiles', 1);

# Run the simulation
run;

# Calculate transmission
wavelengths = getresult('Transmission_Monitor', 'f')';
T_data = getresult('Transmission_Monitor', 'T')';
Transmissions = T_data/max(T_data);
```

