private void DrawCircle(int x1, int y1, int x2, int y2)
{
pictureBox1.Image = (Image)new Bitmap(pictureBox1.Width, pictureBox1.Height);
Graphics g = Graphics.FromImage(pictureBox1.Image);
int r = (int)Math.Sqrt(Math.Pow(x2 - x1, 2) + Math.Pow(y2 - y1, 2));

int x = r;
int y = 0;
int F = 1 - r;
int delFs = 3;
int delFd = 5 - 2 * r;
while (x >= y)
{
g.FillRectangle(Brushes.Black, x1 + x, y1 + y, 1, 1);
g.FillRectangle(Brushes.Black, x1 + y, y1 + x, 1, 1); 
g.FillRectangle(Brushes.Black, x1 - y, y1 + x, 1, 1); 
g.FillRectangle(Brushes.Black, x1 - x, y1 + y, 1, 1); 
g.FillRectangle(Brushes.Black, x1 - x, y1 - y, 1, 1); 
g.FillRectangle(Brushes.Black, x1 - y, y1 - x, 1, 1); 
g.FillRectangle(Brushes.Black, x1 + y, y1 - x, 1, 1); 
g.FillRectangle(Brushes.Black, x1 + x, y1 - y, 1, 1); 

if (F > 0)
{
// Diagonal move.
F += delFd;
x--;
y++;
delFs += 2;
delFd += 4;
}
else
{
// Vertical move.
F += delFs;
y++;
delFs += 2;
delFd += 2;
}
}

pictureBox1.Invalidate();
pictureBox1.Update();
}

}
}

function [fig, best_fit, details] = regression(xval, yval, m)

n = length(xval);

% Linear model
for i = 1:m
    p = polyfit(xval, yval, i); % get polynomial coefficients
    y_est_lin = polyval(p, xval); % calculate estimated y values

    RMSE_lin(i) = sqrt( sum( (yval - y_est_lin).^2 ) / n ); % calculate RMSE
    details(1).coefs{i,1} = p'; % coefficient for each linear order in cell

end
details(1).order = 1:m;
details(1).model = 'linear';
details(1).RMSE = RMSE_lin;

% Non-linear model
x = xval;
y = yval;
logy = log(y);
alog = sum(x.*logy) - sum(x)*sum(logy)/n;
blog = sum(x.^2) - sum(x).^2/n;
b = alog / blog;
c = exp((sum(logy)-b*sum(x))/n);
y_est_nonlin = c*exp(b*x);
RMSE_nonlin = sqrt( sum( (yval - y_est_nonlin).^2 ) / n );

details(2).model = 'non-linear';
details(2).coefs = [c b];
details(2).order = 'n/a';
details(2).RMSE = RMSE_nonlin;

% RMSE comparison
[~, best_fit_ind] = min([RMSE_lin RMSE_nonlin]);
if best_fit_ind <= m
    best_fit = sprintf('linear-%d', best_fit_ind);
else
    best_fit = 'non-linear';
end

fig = figure;

xvals = linspace(min(xval),max(xval),50);
hold on;
plot(xval, yval, 'ro');
for i = 1:m
    p = details(1).coefs{i,1};
    y_plot_lin = polyval(p, xvals);
    plot(xvals, y_plot_lin);
end
y_plot_nonlin = c*exp(b*xvals);
plot(xvals, y_plot_nonlin);

axis([min(xval) max(xval) min(yval) max(yval)]);
xlabel('x');
ylabel('y');
hold off;
end

