```cpp
TArray<FGameplayAbilitySpec*> AYourCharacter::GetActiveAbilitiesWithTags(FGameplayTagContainer AbilityTags) const
{
    TArray<FGameplayAbilitySpec*> ActiveAbilities;

    const UAbilitySystemComponent* AbilitySystemComponent = GetAbilitySystemComponent(); // You may have a different function to get the AbilitySystemComponent.

    if (AbilitySystemComponent)
    {
        TArray<FGameplayAbilitySpec*> AllActiveAbilities;
        AbilitySystemComponent->GetActiveAbilitiesWithTags(AbilityTags, AllActiveAbilities);

        // Filter the active abilities based on your criteria (e.g., certain tags)
        for (FGameplayAbilitySpec* ActiveAbilitySpec : AllActiveAbilities)
        {
            if (ActiveAbilitySpec->HasAllOfTags(AbilityTags))
