public static string HtmlEncode(string input)
{
    StringBuilder sb = new StringBuilder();
    foreach (char c in input)
    {
        switch (c)
        {
            case '<':
                sb.Append("&lt;");
                break;
            case '>':
                sb.Append("&gt;");
                break;
            case '&':
                sb.Append("&amp;");
                break;
            case '\"':
                sb.Append("&quot;");
                break;
            case '\'':
                sb.Append("&apos;");
                break;
            default:
                sb.Append(c);
                break;
        }
    }
    return sb.ToString();
}
