Startup.cs

```
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Builder;
using Microsoft.AspNetCore.Hosting;
using Microsoft.AspNetCore.HttpsPolicy;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;
using Microsoft.Extensions.Logging;
using Senparc.CO2NET;
using Senparc.CO2NET.Extensions;
using Senparc.Weixin.Entities;
using Senparc.Weixin.MP;
using Senparc.Weixin.MP.DependencyInjection;
using Senparc.Weixin.WxOpen;
using Senparc.Weixin.WxOpen.Entities;

namespace WeChatDemo
{
    public class Startup
    {
        public Startup(IConfiguration configuration)
        {
            Configuration = configuration;

            //微信配置
            var appId = Configuration["WeixinOfficialAccount:AppId"];//在appsettings.json中配置
            var appSecret = Configuration["WeixinOfficialAccount:AppSecret"];
            var token = Configuration["WeixinOfficialAccount:Token"];
            var EncodingAESKey = Configuration["WeixinOfficialAccount:EncodingAESKey"];

            var senparcSetting = new SenparcSetting(null)
            {
                IsDebug = true,
                DefaultCacheNamespace = "DefaultCache",
                //配置数据库连接字符串
            };

            var registerService = Senparc.Weixin.RegisterService.Start(senparcSetting).UseSenparcGlobal();
            registerService.UseSenparcWeixin(null, w =>
            {
                w.UseWeixinTraceLog();//提供基于CO2NET的日志记录功能
                w.UseOfficialAccount(appId, appSecret, token, EncodingAESKey);//注册公众号
            });
        }

        public IConfiguration Configuration { get; }

        public void ConfigureServices(IServiceCollection services)
        {
            services.AddControllers();

            services.AddSenparcGlobalServices(Configuration)//Senparc.CO2NET 全局注册
                .AddMemoryCache();//添加内存缓存配置

            services.AddSenparcWeixinServices(Configuration);//Senparc.Weixin 注册
        }

        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
        {
            if (env.IsDevelopment())
            {
                app.UseDeveloperExceptionPage();
            }

            app.UseRouting();

            app.UseEndpoints(endpoints =>
            {
                endpoints.MapControllers()
                    .RequireAuthorization();//加入授权验证
            });

            //启用中间件，处理公众号消息
            app.UseWeiXinOfficialAccountMiddleware(options =>
            {
                options.Token = Configuration["WeixinOfficialAccount:Token"];//设置 Token
                options.AppId = Configuration["WeixinOfficialAccount:AppId"];//设置 AppId

                //配置路由
                options.RouteBasePath = "/weixin";
                options.AppSecret = Configuration["WeixinOfficialAccount:AppSecret"];//设置AppSecret
                options.EncodingAESKey = Configuration["WeixinOfficialAccount:EncodingAESKey"];//设置 EncodingAESKey
                options.MessageHandler = new WeChatMessageHandler();//自定义的消息处理类，继承自 IMessageHandler
                options.MaxRecordCount = 1000;//设置消息去重的最大记录数
                options.EnableMessageDeduplication = true;//启用消息去重功能
            });
        }
    }
}
