using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using WeChat.OfficialAccount.Messages;
using WeChat.OfficialAccount.Services;

namespace YourAppName.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class WeChatController : ControllerBase
    {
        private readonly IWeChatOfficialAccountService _wechatService;

        public WeChatController(IWeChatOfficialAccountService wechatService)
        {
            _wechatService = wechatService;
        }

        [HttpPost]
        public async Task<IActionResult> Post([FromForm] string signature, [FromForm] string timestamp, [FromForm] string nonce, [FromForm] string echostr)
        {
            // 如果校验通过
            if (await _wechatService.CheckSignature(signature, timestamp, nonce))
            {
                // 如果是首次接入，返回echostr
                if (!string.IsNullOrEmpty(echostr))
                {
                    return Content(echostr);
                }

                // 处理消息
                var request = await Request.ReadFormAsync();

                var message = await _wechatService.ProcessMessage(request);

                if (message != null)
                {
                    return Content(message.ToXml());
                }
            }

            return Content("");
        }

        [HttpGet]
        public IActionResult Get()
        {
            return Ok("OK");
        }
    }
}
``` 

