class TrainSchedulingSystem:
    def __init__(self):
        self.admin_logged_in = False
        self.timetable = {}
        self.turnaround_time = 0
        self.stopover_time = 0

    def admin_login(self, password):
        if password == "admin_password":
            self.admin_logged_in = True
            print("Admin logged in successfully.")
        else:
            print("Invalid password. Admin login failed.")

    def create_timetable(self, timetable):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        self.timetable = timetable
        print("Timetable created successfully.")

    def update_timetable(self, station, timings):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        if station not in self.timetable:
            print(f"Station {station} does not exist.")
            return

        self.timetable[station].update(timings)
        print(f"Timetable for station {station} updated successfully.")

    def add_station(self, station):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        if station in self.timetable:
            print(f"Station {station} already exists.")
            return

        self.timetable[station] = {"arrival_time": None, "departure_time": None}
        print(f"Station {station} added successfully.")

    def remove_station(self, station):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        if station not in self.timetable:
            print(f"Station {station} does not exist.")
            return

        del self.timetable[station]
        print(f"Station {station} removed successfully.")

    def set_turnaround_time(self, turnaround_time):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        self.turnaround_time = turnaround_time
        print(f"Turnaround time set to {turnaround_time} minutes.")

    def set_stopover_time(self, stopover_time):
        if not self.admin_logged_in:
            print("Admin login required.")
            return

        self.stopover_time = stopover_time
        print(f"Stopover time set to {stopover_time} minutes.")

    def view_timetable(self):
        return self.timetable

    def travelling_duration(self, departure_station, arrival_station):
        if departure_station not in self.timetable or arrival_station not in self.timetable:
            print("Invalid station(s).")
            return

        departure_time = self.timetable[departure_station]["departure_time"]
        arrival_time = self.timetable[arrival_station]["arrival_time"]

        if None in [departure_time, arrival_time]:
            print("Missing timing data.")
            return

        duration = arrival_time - departure_time

        if duration < 0:
            print("Invalid timing data.")
            return

        return duration

    def train_arrival_departure_time(self, station):
        if station not in self.timetable:
            print("Invalid station.")
            return

        arrival_time = self.timetable[station]["arrival_time"]
        departure_time = self.timetable[station]["departure_time"]

        if None in [arrival_time, departure_time]:
            print("Missing timing data.")
            return

