#include <dirent.h>
#include <stdio.h>
#include <sys/stat.h>
#include <sys/types.h>

void traverse(char *path) {
    DIR *dir;
    struct dirent *entry;
    struct stat fileStat;

    if ((dir = opendir(path)) == NULL) {
        perror("Cannot open folder");
        return;
    }

    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_DIR) {
            if (strcmp(entry->d_name, ".") != 0 && strcmp(entry->d_name, "..") != 0) {
                printf("%s/%s\n", path, entry->d_name);
                traverse(entry->d_name);
            }
        }
        else {
            printf("%s/%s\n", path, entry->d_name);
            stat(path, &fileStat);
            printf("inode: %d\n", fileStat.st_ino);
        }
    }

    closedir(dir);
}

int main(int argc, char **argv) {
    if (argc != 2) {
        printf("Please supply a directory name\n");
        return 1;
    }

    traverse(argv[1]);

    return 0;
}
