                    "id INTEGER PRIMARY KEY AUTOINCREMENT,"\
                    "parent_id INTEGER,"\
                    "name TEXT NOT NULL,"\
                    "type TEXT NOT NULL,"\
                    "size INTEGER,"\
                    "FOREIGN KEY (parent_id) REFERENCES fs_entry (id)"\
                    ");";

  int rc = sqlite3_exec(db, sql.c_str(), callback, 0, &error_message);

