class FileSystem {
  private:
    sqlite3 *db;

  public:
    FileSystem() {
      sqlite3_open("directory_structure.db", &db);
    }

    ~FileSystem() {
      sqlite3_close(db);
    }

    // Inserts a new record into the fs_entry table.
    int insert_fs_entry(int parent_id, const std::string& name, const std::string& entry_type, int size) {
      std::ostringstream sql;
      char* error_message = 0;

      sql << "INSERT INTO fs_entry (parent_id, name, type, size) VALUES "
          << "(" << parent_id << ", '" << name << "', '" << entry_type
          << "', " << size << ");";

      int rc = sqlite3_exec(db, sql.str().c_str(), 0, 0, &error_message);

      if (rc != SQLITE_OK) {
        sqlite3_free(error_message);
        return -1;
      }

      return sqlite3_last_insert_rowid(db);
    }

    // Removes a record from the fs_entry table.
    void remove_fs_entry(int id) {
      std::ostringstream sql;
      char* error_message = 0;

      sql << "DELETE FROM fs_entry WHERE id = " << id << ";";

      sqlite3_exec(db, sql.str().c_str(), 0, 0, &error_message);
      sqlite3_free(error_message);
    }

    // Adds a file.
    void add_file(int parent_id, const std::string& file_name, int size) {
      insert_fs_entry(parent_id, file_name, "file", size);
    }

    // Removes a file.
    void remove_file(int id) {
      remove_fs_entry(id);
    }

    // Adds a directory.
    int add_directory(int parent_id, const std::string& directory_name) {
      return insert_fs_entry(parent_id, directory_name, "directory", 0);
