// Initializes database connection
void initializeDatabase() {
    int rc;
    char* errmsg;
    rc = sqlite3_open("metadata.db", &db);

    if (rc) {
        fprintf(stderr, "Can't open database: %s\n", sqlite3_errmsg(db));
        exit(1);
    } else {
        fprintf(stderr, "Opened database successfully\n");
    }

    // Create table
    char* sql = "CREATE TABLE IF NOT EXISTS metadata (id INTEGER PRIMARY KEY, name TEXT, parentId INTEGER, size INTEGER, isDirectory INTEGER)";
    rc = sqlite3_exec(db, sql, NULL, 0, &errmsg);

    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }
}

// Adds a file to the metadata table
void addFile(char* name, int parentId, long size, int isDirectory) {
    int rc;
    char* errmsg;
    char* sql = "INSERT INTO metadata (name, parentId, size, isDirectory) VALUES (?, ?, ?, ?)";
    sqlite3_stmt* stmt;

    rc = sqlite3_prepare_v2(db, sql, -1, &stmt, NULL);
    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }

    sqlite3_bind_text(stmt, 1, name, -1, SQLITE_STATIC);
    sqlite3_bind_int(stmt, 2, parentId);
    sqlite3_bind_int(stmt, 3, size);
    sqlite3_bind_int(stmt, 4, isDirectory);

    rc = sqlite3_step(stmt);
    if (rc != SQLITE_DONE) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }

    sqlite3_finalize(stmt);
}

// Deletes a file from the metadata table
void deleteFile(int id) {
    int rc;
    char* errmsg;
    char* sql = "DELETE FROM metadata WHERE id = ?";
    sqlite3_stmt* stmt;

    rc = sqlite3_prepare_v2(db, sql, -1, &stmt, NULL);
    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }

    sqlite3_bind_int(stmt, 1, id);

    rc = sqlite3_step(stmt);
    if (rc != SQLITE_DONE) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }

    sqlite3_finalize(stmt);
}

// Adds a folder to the metadata table
void addFolder(char* name, int parentId) {
    addFile(name, parentId, 0, 1);
}

// Deletes a folder and its contents from the metadata table
void deleteFolder(int id, int recursive) {
    char* sql = "SELECT * FROM metadata WHERE parentId = ?";
    sqlite3_stmt* stmt;
    int rc;
    int i;

    // Get all files and folders inside the folder
    rc = sqlite3_prepare_v2(db, sql, -1, &stmt, NULL);
    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", sqlite3_errmsg(db));
        exit(1);
    }

    sqlite3_bind_int(stmt, 1, id);

    while (sqlite3_step(stmt) == SQLITE_ROW) {
        int childId = sqlite3_column_int(stmt, 0);
        int isDirectory = sqlite3_column_int(stmt, 4);

        if (isDirectory && recursive) {
            deleteFolder(childId, recursive);
        } else {
            deleteFile(childId);
        }
    }

    sqlite3_finalize(stmt);

    // Delete the folder itself
    deleteFile(id);
}

// Recursive function to calculate folder size
long getFolderSize(int folderId) {
    long size = 0;
    char* sql = "SELECT * FROM metadata WHERE parentId = ?";
    sqlite3_stmt* stmt;
    int rc;

    // Get all files inside the folder
    rc = sqlite3_prepare_v2(db, sql, -1, &stmt, NULL);
    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", sqlite3_errmsg(db));
        exit(1);
    }

    sqlite3_bind_int(stmt, 1, folderId);

    while (sqlite3_step(stmt) == SQLITE_ROW) {
        int isDirectory = sqlite3_column_int(stmt, 4);

        if (isDirectory) {
            size += getFolderSize(sqlite3_column_int(stmt, 0));
        } else {
            size += sqlite3_column_int(stmt, 3);
        }
    }

    sqlite3_finalize(stmt);

    return size;
}

