DirectoryMetadata::DirectoryMetadata(const std::string &db_path) {
    if (sqlite3_open(db_path.c_str(), &db) != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }

    // Create tables if they don't exist
    std::string create_dir_table = "CREATE TABLE IF NOT EXISTS directories (id INTEGER PRIMARY KEY, parent_id INTEGER REFERENCES directories(id), name TEXT NOT NULL UNIQUE);";
    std::string create_file_table = "CREATE TABLE IF NOT EXISTS files (id INTEGER PRIMARY KEY, directory_id INTEGER REFERENCES directories(id), name TEXT NOT NULL, size INTEGER NOT NULL);";

    if (sqlite3_exec(db, create_dir_table.c_str(), nullptr, nullptr, nullptr) != SQLITE_OK) {
        throw std::runtime_error("Error creating directories table");
    }

    if (sqlite3_exec(db, create_file_table.c_str(), nullptr, nullptr, nullptr) != SQLITE_OK) {
        throw std::runtime_error("Error creating files table");
