bool DirectoryMetadata::process_directory(const std::string &dir_path, bool add) {
    if (!fs::is_directory(dir_path)) {
        throw std::runtime_error("Not a directory: " + dir_path);
    }

    sqlite3_stmt *stmt;
    if (add) {
        stmt = prepare_statement("INSERT INTO directories (name) VALUES (?);");
    } else {
        stmt = prepare_statement("DELETE FROM directories WHERE name = ?;");
    }

    sqlite3_bind_text(stmt, 1, dir_path.c_str(), -1, 0);

    bool success = sqlite3_step(stmt) == SQLITE_DONE;
    finalize_statement(stmt);
    return success;
}

bool DirectoryMetadata::process_file(const std::string &file_path, bool add) {
    if (!fs::is_regular_file(file_path)) {
        throw std::runtime_error("Not a file: " + file_path);
    }

    fs::path p(file_path);
    fs::path dir = p.parent_path();
    fs::path file = p.filename();
    long long dir_id = get_directory_id(dir.string());

    if (dir_id == -1) {
        throw std::runtime_error("Directory not found: " + dir_path);
    }

    sqlite3_stmt *stmt;
    if (add) {
        stmt = prepare_statement("INSERT INTO files (directory_id, name, size) VALUES (?, ?, ?);");
        sqlite3_bind_int64(stmt, 3, fs::file_size(file_path));
    } else {
        stmt = prepare_statement("DELETE FROM files WHERE directory_id = ? AND name = ?;");
    }

    sqlite3_bind_int64(stmt, 1, dir_id);
    sqlite3_bind_text(stmt, 2, file.string().c_str(), -1, 0);

    bool success = sqlite3_step(stmt) == SQLITE_DONE;
    finalize_statement(stmt);
    return success;
}

long long DirectoryMetadata::get_file_size(const std::string &file_path) {
    if (!fs::is_regular_file(file_path)) {
