            "name TEXT NOT NULL,"
            "parent_id INTEGER NOT NULL,"
            "level INTEGER NOT NULL,"
            "size INTEGER,"
            "is_file INTEGER NOT NULL,"
            "is_dir INTEGER NOT NULL"
            ");";

        char* error_msg;
        int result = sqlite3_exec(db_, sql, nullptr, 0, &error_msg);
        if (result != SQLITE_OK) {
            throw runtime_error(string("Failed to create table: ") + error_msg);
        }
    }

